import UIKit

protocol CameraPresenterInput
{
    func presentSomething(response: CameraResponse)
}

protocol CameraPresenterOutput: class
{
    func displaySomething(viewModel: CameraViewModel)
}

class CameraPresenter: CameraPresenterInput
{
    weak var output: CameraPresenterOutput!
    
    // MARK: Presentation logic
    
    func presentSomething(response: CameraResponse)
    {
        // NOTE: Format the response from the Interactor and pass the result back to the View Controller
        
        let viewModel = CameraViewModel()
        output.displaySomething(viewModel)
    }
}
