import UIKit

enum TouristPresenterError: ErrorType {
    case General
}

class TouristPresenter: TouristPresenterInput
{
    weak var output: TouristPresenterOutput!

    // MARK: - Presentation logic
    func presentTouristDetail(response: Response<Tourist_FetchDetail_Response>)
    {
        switch response {
        case .Error(let error):
            output.displayTouristDetail(ViewModel.Error(error))
            break
        case .Result(let result):
            let viewModel = Tourist_FetchDetail_ViewModel.viewModelFromDictonary(result.detail)!
            output.displayTouristDetail(ViewModel.Result(viewModel))
            break
        }
    }
}
