import UIKit

// MARK: - Connect View, Interactor, and Presenter

typealias TripInteractorInput = TripViewControllerOutput
typealias TripPresenterInput = TripInteractorOutput
typealias TripPresenterOutput = TripViewControllerInput

class TripConfigurator
{
    // MARK: - Object lifecycle
    
    class var sharedInstance: TripConfigurator
    {
        struct Static {
            static var instance: TripConfigurator?
            static var token: dispatch_once_t = 0
        }
        
        dispatch_once(&Static.token) {
            Static.instance = TripConfigurator()
        }
        
        return Static.instance!
    }
    
    // MARK: - Configuration
    
    func configure(viewController: TripViewController)
    {
        let router = TripRouter()
        router.viewController = viewController
        
        let presenter = TripPresenter()
        presenter.output = viewController
        
        let interactor = TripInteractor()
        interactor.output = presenter
        
        viewController.output = interactor
        viewController.router = router
    }
}
