import UIKit

// MARK: - Connect View, Interactor, and Presenter
typealias TripEditInteractorInput = TripEditViewControllerOutput
typealias TripEditPresenterInput = TripEditInteractorOutput
typealias TripEditPresenterOutput = TripEditViewControllerInput

class TripEditConfigurator
{
    // MARK: - Object lifecycle
    class var sharedInstance: TripEditConfigurator
    {
        struct Static {
            static var instance: TripEditConfigurator?
            static var token: dispatch_once_t = 0
        }
        
        dispatch_once(&Static.token) {
            Static.instance = TripEditConfigurator()
        }
        
        return Static.instance!
    }
    
    // MARK: - Configuration
    func configure(viewController: TripEditViewController)
    {
        let router = TripEditRouter()
        router.viewController = viewController
        
        let presenter = TripEditPresenter()
        presenter.output = viewController
        
        let interactor = TripEditInteractor()
        interactor.output = presenter
        
        viewController.output = interactor
        viewController.router = router
    }
}
